import Togglable from './Togglable'

const Blog = ({ blog, handleLike, handleRemove }) => {
  /*
  const blogStyle = {
    paddingTop: 10,
    paddingLeft: 2,
    border: 'solid',
    borderWidth: 1,
    marginBottom: 5
  }
  */

  const togglableStyle = {
    display: 'inline'
  }

  const updateBlog = async () => {
    const updatedBlog = { ...blog }
    updatedBlog.likes = blog.likes + 1
    await handleLike(updatedBlog)
  }

  const removeBlog = async () => {
    await handleRemove(blog.id)
  }

  return (
    <div className="blog">
      {blog.title} by {blog.author}
      <Togglable className="blogDetails" style={togglableStyle} buttonLabel="view" hideButtonLabel="hide">
        <div>{blog.url}</div>
        <div>likes {blog.likes}<button onClick={updateBlog}>like</button></div>
        <div>{blog.user.name}</div>
        <div><button onClick={removeBlog}>Remove</button></div>
      </Togglable>
    </div>
  )
}

export default Blog
